import { Component } from '@angular/core';
import { User } from '../Models/User';
import { UserService } from '../Services/user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-registration',
  templateUrl: './user-registration.component.html',
  styleUrls: ['./user-registration.component.css']
})
export class UserRegistrationComponent {

  user: User;

  constructor(private _service: UserService, private Route: Router) {}


  onSubmit(form: any): void {
    this.user={
      "userId": 0,
  "userName": "string",
  "email": "string",
  "password": "string",
  "phoneNumber": "string",
  "status": "string",
  "userRole": "string"
    }

    this._service.UserRegistration(this.user).subscribe(data =>{
      console.log(this.user);
    });
    // if (form.valid) {
    //   this._service.UserRegistration(this.user).subscribe({
    //     next: () => {
    //       alert("User Registered Successfully");
    //       this.Route.navigate(['userLogin']);
    //     },
    //     error: () => {
    //       alert("User Registration Failed");
    //     }
    //   });
    // } else {
    //   alert("Invalid user Information");
    // }
  }
}
<div class="container">
  <form #userForm="ngForm" (ngSubmit)="onSubmit(userForm.value)">
    <div class="col text-center p-2 mb-3 bg-light">
      <span>User Registration</span>
    </div>
    <div class="form-group">
      <label for="userName">Username:</label>
      <input #UserName type="text" class="form-control" id="userName" name="userName" [(ngModel)]="user.userName" required minlength="3"/>
    </div>
    <div class="form-group">
      <label for="email">Email:</label>
      <input type="email" class="form-control" id="email" name="email" [(ngModel)]="user.email" required email/>
    </div>
    <div class="form-group">
      <label for="password">Password:</label>
      <input type="password" class="form-control" id="password" name="password" [(ngModel)]="user.password" required minlength="6"/>
    </div>
    <div class="form-group">
      <label for="phoneNumber">Phone Number:</label>
      <input type="text" class="form-control" id="phoneNumber" name="phoneNumber" [(ngModel)]="user.phoneNumber" required/>
    </div>
    <div class="form-group">
      <label for="status">Status:</label>
      <input type="text" class="form-control" id="status" name="status" [(ngModel)]="user.status" readonly/>
    </div>
    <div class="form-group">
      <label for="userRole">User Role:</label>
      <select name="userRole" id="userRole" [(ngModel)]="user.userRole" required>
        <option value="seller">Seller</option>
        <option value="customer">Customer</option>
      </select>
    </div>
    <button type="submit" class="btn btn-primary" [disabled]="userForm.invalid">Register</button>
  </form>
</div>
