import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Order } from 'src/app/Models/Order';
import { Product } from 'src/app/Models/Product';
import { ProductService } from 'src/app/Services/product.service';

@Component({
  selector: 'app-productbuy',
  templateUrl: './productbuy.component.html',
  styleUrls: ['./productbuy.component.css']
})
export class ProductbuyComponent {
  product:Product;

  constructor(private service:ProductService, private router:Router){}

  ngOnInit(prodid:number){
    this.service.getProductById(prodid).subscribe(data => {
      this.product = data;
      console.log(this.product);
    })
  }
}
<div class="container mt-5">
    <div class="row">
        <div class="col-md-12">
            <div class="card mb-4 shadow-sm">
                <img [src]="product.image" class="card-img-top" alt="{{ product.prodName }}">
                <div class="card-body">
                    <h5 class="card-title">{{ product.prodName }}</h5>
                    <p class="card-text">{{ product.description }}</p>
                    <p class="card-text"><small class="text-muted">Price: ₹{{ product.price }}</small></p>
                    <p class="card-text"><small class="text-muted">Stock Quantity: {{ product.stock_Quantity }}</small></p>
                    <!-- <button type="button" class="btn btn-primary" (click)="orderNow()">Order Now</button> -->
                </div>
            </div>
        </div>
    </div>
</div>
