import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import { catchError } from 'rxjs/operators';
import { Product } from 'src/app/Models/Product';
import { ProductService } from 'src/app/Services/product.service';

@Component({
  selector: 'app-productadd',
  templateUrl: './productadd.component.html',
  styleUrls: ['./productadd.component.css']
})
export class ProductaddComponent implements OnInit {
  productForm: FormGroup;
  product: Product;

  constructor(private fb: FormBuilder, private service: ProductService, private router: Router) { }

  ngOnInit(): void {
    this.productForm = this.fb.group({
      prodId: [0],
      prodName: [''],
      description: [''],
      image: [''],
      availaibility: [''],
      categoryId: [''],
      userId: [''],
      price: [0],
      stock_Quantity: [0]
    });
  }

  onSubmit(form: any) {
    this.productForm = form;
    console.log('Form Values:', this.productForm.value);
    if (this.productForm.valid) {
      const formValues = this.productForm.value;
      this.product = {
        prodId: formValues.prodId,
        prodName: formValues.prodName,
        description: formValues.description,
        image: formValues.image,
        availaibility: formValues.availaibility,
        categoryId: formValues.categoryId,
        userId: formValues.userId,
        price: formValues.price,
        stock_Quantity: formValues.stock_Quantity
      };

      console.log('Product to be added:', this.product);

      this.service.AddProduct(this.product).subscribe(
        response => {
          console.log('Product Added successfully!', response);
          this.router.navigate(['productList']);
        },
        error => {
          console.error('Error Adding Product', error);
        }
      );
    }
  }
}

<div class="container">
  <form [formGroup]="productForm" (ngSubmit)="onSubmit(productForm)">
    <div>
      <h2>Product Form</h2>
    </div>

    <div class="form-group">
      <label for="prodName">Product Name</label>
      <input type="text" id="prodName" name="prodName" class="form-control" formControlName="prodName">
    </div>

    <div class="form-group">
      <label for="description">Description</label>
      <input type="text" id="description" name="description" class="form-control" formControlName="description">
    </div>

    <div class="form-group">
      <label for="image">Image URL</label>
      <input type="text" id="image" name="image" class="form-control" formControlName="image">
    </div>

    <div class="form-group">
      <label for="availablility">Availability</label>
      <input type="text" id="availablility" name="availablility" class="form-control" formControlName="availablility">
    </div>

    <div class="form-group">
      <label for="categoryId">Category ID</label>
      <input type="text" id="categoryId" name="categoryId" class="form-control" formControlName="categoryId">
    </div>

    <div class="form-group">
      <label for="userId">User ID</label>
      <input type="text" id="userId" name="userId" class="form-control" formControlName="userId">
    </div>

    <div class="form-group">
      <label for="price">Price</label>
      <input type="number" id="price" name="price" class="form-control" formControlName="price">
    </div>

    <div class="form-group">
      <label for="stock_Quantity">Stock Quantity</label>
      <input type="number" id="stock_Quantity" name="stock_Quantity" class="form-control" formControlName="stock_Quantity">
    </div>

    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
