import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Product } from 'src/app/Models/Product';
import { ProductService } from 'src/app/Services/product.service';

@Component({
  selector: 'app-productadd',
  templateUrl: './productadd.component.html',
  styleUrls: ['./productadd.component.css']
})
export class ProductaddComponent {
  productForm:FormGroup;
  product : Product;

  constructor(private fb:FormBuilder,private service:ProductService,private router:Router){}
  ngOnInit():void{
    this.productForm = this.fb.group({
      prodId: [0],
      prodName: [''],
      description: [''],
      image: [''],
      availaibility: [''],
      categoryId:[''],
      userId: [''],
      price: [0],
      stock_Quantity: [0]
      })
  }

  onSubmit(form: any) {
    this.productForm=form;
    console.log(this.productForm.value)
    if (this.productForm.valid) {
      const formValues = this.productForm.value;
      this.product = {
        prodId:formValues.prodId,
        prodName:formValues.prodName,
        description: formValues.description,
        image: formValues.image,
        availaibility: formValues.availaibility,
        categoryId: formValues.categoryId,
        userId: formValues.userId,
        price: formValues.price,
        stock_Quantity: formValues.stock_Quantity
      };

      this.service.ProductAdd(this.product).subscribe(
        response => {
          console.log('Product Added successfully!', response);
          this.router.navigate(['prodList']);
        },
        error => {
          console.error('Error Adding Product', error);
        }
      );
    // console.log('Form Submitted!', form.value);
    // console.log(this.product.prodId);
    // this.service.ProductAdd(this.product).subscribe(response => {
    //   console.log('Product Added successfully!', response);
    //       this.router.navigate(['prodList']);
    //     },
    //     error => {
    //       console.error('Error Adding Product!', error);
    // })
  }
}
}
