import { HttpClient, HttpErrorResponse, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Order } from '../Models/Order';
import { catchError, Observable, throwError } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class OrderService {
  apiUrl: string = 'http://localhost:5145/Order'; // Ensure the API path is correct
  httpOptions = { headers: new HttpHeaders({ 'Content-type': 'application/json' }) };

  constructor(private http: HttpClient, private activeRoute: ActivatedRoute) { }

  // Add Order
  addOrder(order: Order): Observable<any> {
    console.log("Order: " + order);
    return this.http.post<any>(this.apiUrl, order, this.httpOptions)
      .pipe(catchError(this.handleError));
  }

  // Get All Orders
  getAllOrders(): Observable<Order[]> {
    return this.http.get<Order[]>(this.apiUrl)
      .pipe(catchError(this.handleError));
  }

  // Get Order By ID
  getOrderById(id: number): Observable<Order> {
    return this.http.get<Order>(`${this.apiUrl}/${id}`)
      .pipe(catchError(this.handleError));
  }

  // Get Orders By User ID
  getOrdersByUserId(userid: number): Observable<Order[]> {
    return this.http.get<Order[]>(`${this.apiUrl}/GetOrdersByUserId/${userid}`)
      .pipe(catchError(this.handleError));
  }

  // Update Order
  updateOrder(id: number, order: Order): Observable<Order> {
    return this.http.put<Order>(`${this.apiUrl}/${id}`, order, this.httpOptions)
      .pipe(catchError(this.handleError));
  }

  // Delete Order
  deleteOrder(id: number): Observable<any> {
    return this.http.delete<any>(`${this.apiUrl}/${id}`)
      .pipe(catchError(this.handleError));
  }

  // Error Handling
  private handleError(error: HttpErrorResponse): Observable<never> {
    let errorMessage: string;
    if (error.error instanceof ErrorEvent) {
      // Client-side error
      errorMessage = `Client-side error: ${error.error.message}`;
    } else {
      // Server-side error
      errorMessage = `Server-side error: ${error.status}\nMessage: ${error.message}`;
    }
    console.error(errorMessage);
    alert(errorMessage);
    return throwError(() => new Error(errorMessage));
  }
}
