import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { User } from 'src/app/Models/User';
import { UserService } from 'src/app/Services/user.service';

@Component({
  selector: 'app-userdetails',
  templateUrl: './userdetails.component.html',
  styleUrls: ['./userdetails.component.css']
})
export class UserdetailsComponent {
  user: User;
  Id:number;
  constructor(private _user:UserService,private activeroute:ActivatedRoute){}

  OnInit(){
    this.Id = parseInt(sessionStorage.getItem('userid') || '0',10);   // if session value is null assigns it to 0, and 10 is to use it as base for the conversion

    // let Id=this.activeroute.snapshot.params['id'];
    console.log("From UserDetails Component ==> " +this.Id);
    this._user.getUserById(this.Id).subscribe(data =>{
      this.user = data;
      console.log(data);
    })
  }
}
