import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from 'src/app/Models/Product';
import { ProductService } from 'src/app/Services/product.service';

@Component({
  selector: 'app-productedit',
  templateUrl: './productedit.component.html',
  styleUrls: ['./productedit.component.css']
})
export class ProducteditComponent {
  product: Product;
  prodId:number;
  constructor(private service:ProductService,private activateRoute:ActivatedRoute,private route:Router){}
  ngOnInit(){
    this.prodId = this.activateRoute.snapshot.params['id'];
    console.log("id = " + this.prodId);
    this.service.getProductById(this.prodId).subscribe(data => {
      this.product = data;
      console.log(this.product);
    }
    )
  }

  onSubmit(){
    this.service.UpdateProduct(this.prodId,this.product).subscribe(data =>{
      alert("data Changed!");
      this.route.navigate(['/productlist'])
    },
    error => alert(error.errorMessage)
  );

  }
}
