import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from './Services/user.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'StyleSphere_Angular';
  role: string | null = '';
  loggedIn: boolean = false;

  constructor(private service: UserService, private router: Router) {}

  ngOnInit(): void {
    this.updateLoginStatus();

    // Listen to changes in sessionStorage and localStorage
    window.addEventListener('storage', () => {
      this.updateLoginStatus();
    });
  }

  updateLoginStatus(): void {
    this.role = sessionStorage.getItem("userrole");
    this.loggedIn = localStorage.getItem("logged") === 'true';
  }

  logout(): void {
    sessionStorage.removeItem("userrole");
    sessionStorage.removeItem("loginToken");
    localStorage.removeItem("logged");
    this.updateLoginStatus();
    this.router.navigate(['/userLogin']);
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-primary">
  <div class="container">
    <a class="navbar-brand" routerLink="app">Home</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
      aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav">
        <li *ngIf="loggedIn && role === 'customer'">
          <a class="nav-link active" aria-current="page" routerLink="productList">Products</a>
        </li>
        <li *ngIf="loggedIn && role === 'seller'">
          <a class="nav-link active" aria-current="page" routerLink="productList">My Products</a>
        </li>
        <li class="nav-item" *ngIf="!loggedIn">
          <a class="nav-link active" aria-current="page" routerLink="userRegister">Sign Up</a>
        </li>
        <li class="nav-item" *ngIf="!loggedIn">
          <a class="nav-link active" aria-current="page" routerLink="userLogin">Login</a>
        </li>
        <li class="nav-item" *ngIf="loggedIn"> <a class="nav-link active" aria-current="page" (click)="logout()">Logout</a> </li>
      </ul>
    </div>
  </div>
</nav>
