import { Component } from '@angular/core';
import { User } from '../Models/User';
import { UserService } from '../Services/user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-registration',
  templateUrl: './user-registration.component.html',
  styleUrls: ['./user-registration.component.css']
})
export class UserRegistrationComponent {

  user: User;

  constructor(private _service: UserService, private Route: Router) {}

  onSubmit(form: any): void {
    if (form.valid) {
      this._service.UserRegistration(this.user).subscribe({
        next: () => {
          alert("User Registered Successfully");
          this.Route.navigate(['userLogin']);
        },
        error: () => {
          alert("User Registration Failed");
        }
      });
    } else {
      alert("Invalid user Information");
    }
  }
}
<div class="container">
  <form #user="ngForm" (ngSubmit)="onSubmit(user)" [(ngModel)]="registrationForm">
    <div class="col text-center p-2 mb-3 bg-light">
      <span>User Registration</span>
    </div>
    <div class="form-group">
      <label for="userName">Username:</label>
      <input type="text" class="form-control" id="userName" name="userName" [(ngModel)]="registrationForm.userName" />
      <!-- <div *ngIf="!user.controls.userName.valid && user.controls.userName.dirty" class="alert alert-danger">
        Username is required.
      </div> -->
    </div>
    <div class="form-group">
      <label for="email">Email:</label>
      <input type="email" class="form-control" id="email" name="email" [(ngModel)]="registrationForm.email" />
      <!-- <div *ngIf="!user.controls.email.valid && user.controls.email.dirty" class="alert alert-danger">
        <div *ngIf="user.controls.email.errors.required">Email is required.</div>
        <div *ngIf="user.controls.email.errors.email">Invalid email format.</div>
      </div> -->
    </div>
    <div class="form-group">
      <label for="password">Password:</label>
      <input type="password" class="form-control" id="password" name="password" [(ngModel)]="registrationForm.password" />
      <!-- <div *ngIf="!user.controls.password.valid && user.controls.password.dirty" class="alert alert-danger">
        <div *ngIf="user.controls.password.errors.required">Password is required.</div>
        <div *ngIf="user.controls.password.errors.minlength">Password must be at least 6 characters long.</div>
      </div> -->
    </div>
    <div class="form-group">
      <label for="phoneNumber">Phone Number:</label>
      <input type="text" class="form-control" id="phoneNumber" name="phoneNumber" [(ngModel)]="registrationForm.phoneNumber" />
      <!-- <div *ngIf="!user.controls.phoneNumber.valid && user.controls.phoneNumber.dirty" class="alert alert-danger">
        <div *ngIf="user.controls.phoneNumber.errors.required">Phone Number is required.</div>
        <div *ngIf="user.controls.phoneNumber.errors.pattern">Invalid phone number format.</div>
      </div> -->
    </div>
    <div class="form-group">
      <label for="status">Status:</label>
      <input type="text" class="form-control" id="status" name="status" [(ngModel)]="registrationForm.status" readonly/>
      <!-- <div *ngIf="!user.controls.status.valid && user.controls.status.dirty" class="alert alert-danger">
        Status is required.
      </div> -->
    </div>
    <div class="form-group">
      <label for="userRole">User Role:</label>
      <select name="userRole" id="userRole" [(ngModel)]="registrationForm.userRole">
        <option value="seller">seller</option>
        <option value="customer">customer</option>
      </select>
      <!-- <input type="text" class="form-control" id="userRole" [(ngModel)]="registrationForm.userRole" /> -->
    </div>
    <button type="submit" class="btn btn-primary" [disabled]="user.invalid">Register</button>
  </form>
</div>
